import React, { Component } from 'react';
import './App.css';
import { connect } from 'react-redux';
import {  addToFavourites } from "./actions";
import { withRouter} from 'react-router-dom';
class App extends Component {
  addToFavourites(movieId) {
    const movieToAdd = this.props.movies.find(movie => movie.id === movieId);
    this.props.addToFavourites(movieToAdd);
    this.props.history.push('/favourites');
  }
  render() {
    const movies = this.props.movies;
    return (

        <div className="App">
          <ul>
            <li><a className="active" href="#home">Home</a></li>
            <li><a href="/favourites">Favorite Films</a></li>
          </ul>
            <ul className="list-group">
              {
                movies.map(movie => {
                  return (
                      <li key={movie.id} className="list-group-item" >
                        {movie.name}
                        {movie.image}
                        <button onClick={this.addToFavourites.bind(this, movie.id)}>Add</button>
                      </li>
                  )
                })
              }
            </ul>
          <h1>Movies List</h1>
        </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    movies: state.movies.movies
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addToFavourites: (movie) => {
      dispatch(addToFavourites(movie));
    }
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
